import {useState, useEffect} from "react";
import axios from "axios";

// VO
interface Food {
    fno:number;
    name:string;
    poster:string;

}
// Map에 등록한 내용과 일치
interface FoodListProps {
    curpage:number;
    totalpage:number;
    startPage:number;
    endPage:number;
    list:Food[];
}

function FoodList() {
    const [curpage, setCurpage] = useState<number>(1);
    const [data, setData] = useState<FoodListProps>();

    useEffect(() => {
        axios.get(`http://localhost/food/list_react/${curpage}`)
        .then(res =>{
            console.log(res.data);
            setData(res.data);
        })
    }, [curpage]); // 재호출 [] : mounted(), [변수] -> 재호출

    const html:any=data?.list.map((food:Food)=>
        <div className="col-md-3">
            <div className="thumbnail">
                <a href="#">
                    <img src={food.poster} alt="Lights" style={{"width":"100%","height":"150px"}} />
                    <div className="caption">
                        <p>{food.name}</p>
                    </div>
                </a>
            </div>
        </div>
    )

    return(
        <div className="container">
            <div className="row">
                {html}
            </div>
            <div className="row text-center" style={{"height":"20px"}}></div>
        </div>

    )
}

export default FoodList;
